// encapsulamento serve para proteger o código e definir limites de alterações;namespace Encapsulamento.Models;public class ContaCorrente{    public ContaCorrente(int numeroConta, decimal saldoInicial)    {        NumeroConta = numeroConta;        saldo = saldoInicial;    }        public int NumeroConta { get; set; }        private decimal saldo;        public void Sacar(decimal valor)    {        if (saldo >= valor)        {            saldo -= valor;             Console.WriteLine("Saque realizado com sucesso!");        }        else        {            Console.WriteLine("O valor solicitado é maior que o saldo disponível.");        }    }        public void ExibirSaldo()    {        Console.WriteLine($"Seu saldo disponível é de R$:" + saldo);    }}