namespace Exemplo.Models{    public class Professor : Pessoa    {        public Professor(string nome) : base(nome)        {                }        public decimal Salario { get; set; }        public sealed override void Apresentar() //sealed protege o método de ser usado        {            Console.WriteLine($"Olá meu nome é {Nome} eu tenho {Idade} anos e eu ganho {Salario}");         }    }}