namespace Exemplo.Interfaces;public interface ICalculadora{    int Somar(int num1, int num2);    int Subtrair(int num1, int num2);  // metodos que não possuem corpo são obrigatórios, para implementação.    int Multiplicar(int num1, int num2);    int Dividir(int num1, int num2)    {        return num1 / num2; // metodos que tem corpo implementa um método padrão, nesse caso são opcionais.    }}